package com.mxkj.yuanyintang.pay.ali_payimport android.text.TextUtilsclass PayResult(rawResult: Map<String, String>?) {    /**     * @return the resultStatus     */    var resultStatus: String? = null        private set    /**     * @return the result     */    var result: String? = null        private set    /**     * @return the memo     */    var memo: String? = null        private set    init {        for (key in rawResult!!.keys) {            if (TextUtils.equals(key, "resultStatus")) {                resultStatus = rawResult[key]            } else if (TextUtils.equals(key, "result")) {                result = rawResult[key]            } else if (TextUtils.equals(key, "memo")) {                memo = rawResult[key]            }        }    }}